import React, { useState, useEffect } from 'react';
import {jwtDecode} from 'jwt-decode';
import { Card, Button, Typography, Container, Box } from '@mui/material';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';


const RentalHistoryPage = () => {
  const [rentals, setRentals] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
const [fieldDetails, setFieldDetails] = useState(null);


  useEffect(() => {
    console.log("RentalHistoryPage bileşeni yüklendi.");
    const fetchRentals = async () => {
      try {
        const token = localStorage.getItem('token');
        const decodedToken = jwtDecode(token);
        const userid = decodedToken.id; // JWT'nin yapısına bağlı olarak değişebilir

        const response = await fetch(`http://localhost:4042/rent-football-field/rent-football-field-history?userid=${userid}`, {
          headers: {
            'Authorization': `Bearer ${token}`, // Eğer API token gerektiriyorsa
          },
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setRentals(data);
        setFieldDetails(fieldDetails);
        setOpenDialog(true);
      } catch (error) {
        console.error("Rental history fetch error:", error);
      }
    };

    fetchRentals();
  }, []);

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleViewDetails = async (footballFieldid) => {
    try {
      const response = await fetch(`http://localhost:4042/football-field/${footballFieldid}`);

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const fieldDetails = await response.json();
      
      // Basit bir alert ile halı saha detaylarını göster
      alert(`Halı Saha İsmi: ${fieldDetails.name}\nAdres: ${fieldDetails.address}\nFiyat: ${fieldDetails.price}`);
    } catch (error) {
      console.error("Football field details fetch error:", error);
      alert("Halı saha detayları yüklenirken bir hata oluştu.");
    }
  };

  return (
    <Container maxWidth="md">
      <Box mt={4}>
        {rentals.map((rental, index) => (
          <Card key={index} variant="outlined" sx={{ mb: 2 }}>
            <Typography variant="h6">Kiralama Tarihi: {new Date(rental.startDate).toLocaleString()}</Typography>
            <Typography variant="h6">Bitiş Tarihi: {new Date(rental.endDate).toLocaleString()}</Typography>
            <Button variant="contained" color="primary" onClick={() => handleViewDetails(rental.footballFieldid)}>
              Detayları Gör
            </Button>
          </Card>
        ))}
      </Box>
      <Dialog open={openDialog} onClose={handleCloseDialog}>
    <DialogTitle>Halı Saha Detayları</DialogTitle>
    <DialogContent>
    <Typography variant="h6">İsim: {fieldDetails?.name}</Typography>
          <Typography>Telefon: {fieldDetails?.telephoneNumber}</Typography>
          <Typography>Şehir: {fieldDetails?.city}</Typography>
          <Typography>İlçe: {fieldDetails?.district}</Typography>
          <Typography>Adres: {fieldDetails?.address}</Typography>
          <Typography>Fiyat: {fieldDetails?.price} TL</Typography>
    </DialogContent>
    <DialogActions>
      <Button onClick={handleCloseDialog}>Kapat</Button>
    </DialogActions>
  </Dialog>
    </Container>
    
  );
};

export default RentalHistoryPage;
