import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Typography, CardContent, CardActions, Container, Box } from '@mui/material';
import {jwtDecode} from "jwt-decode";

const RentalHistoryPage = () => {
  const [rentals, setRentals] = useState([]);
  
  useEffect(() => {
    const fetchRentals = async () => {
      try {
        // Token'dan user ID'yi almak için JWT decode kullan
        const token = localStorage.getItem("token");
        const decodedToken = jwtDecode(token);
        const userid = decodedToken.id; 

        const response = await axios.get(`http://localhost:4042/rent-football-field/rent-football-field-history?userid=${userid}`);
        setRentals(response.data);
      } catch (error) {
        console.error("Rental history fetch error:", error);
      }
    };

    fetchRentals();
  }, []);

  const handleViewDetails = async (footballFieldid) => {
    try {
      // Detayları görüntüleme için ayrı bir API isteği yap
      const response = await axios.get(`http://localhost:4042/football-field/${footballFieldid}`);
      // Burada response ile dönen detayları nasıl göstereceğinize karar vermelisiniz
      console.log(response.data); // Örneğin konsola log atabilirsiniz
    } catch (error) {
      console.error("Football field details fetch error:", error);
    }
  };

  return (
    <Container maxWidth="md">
      <Box mt={4}>
        {rentals.map((rental, index) => (
          <Card key={index} variant="outlined" sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6">Kiralama Tarihi: {new Date(rental.startDate).toLocaleString()}</Typography>
              <Typography variant="h6">Bitiş Tarihi: {new Date(rental.endDate).toLocaleString()}</Typography>
            </CardContent>
            <CardActions>
              <Button variant="contained" color="primary" onClick={() => handleViewDetails(rental.footballFieldid)}>
                Detayları Gör
              </Button>
            </CardActions>
          </Card>
        ))}
      </Box>
    </Container>
  );
};

export default RentalHistoryPage;
